    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Hello GameController!{% endblock %}

{% block body %}
    <h1>Liste des jeux</h1>
    <table class=\"table table-bordered\">
        <thead class=\"thead-dark\">
            <tr>
                <th>ID</th>
                <th>Titre</th>
                <th>Joueurs min</th>
                <th>Joueurs max</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
            {% for game in games %}
                <tr>
                    <td>{{game.id}}</td>
                    <td>{{game.title}}</td>
                    <td>{{game.minPlayers}}</td>
                    <td>{{game.maxPlayers}}</td>
                    <td>
                        <a href=\"{{ path('app_admin_game_edit', {id: game.id}) }}\"><i class=\"fa fa-edit\"></i></a>
                        <a href=\"{{ path('app_admin_game_modify', {title: game.title}) }}\"><i class=\"fa fa-pencil\"></i></a>
                        <a href=\"{{ path('app_admin_game_delete', {id: game.id}) }}\"><i class=\"fa fa-trash\"></i></a>
                    </td>
                </tr>
            {% else %}
                <tr>
                    <td colspan=\"4\">Aucun jeu n'est enregistré</td>
                </tr>
            {% endfor %}
        </tbody>
    </table>
{% endblock %}
", "admin/game/index.html.twig", "/Applications/MAMP/htdocs/myscoreboard/templates/admin/game/index.html.twig");
